foreach f in files do {
  print f;
}

var parts = call string.split(text="apple,banana,cherry", regex=",");
foreach part in parts do {
  print part;
}

var arr = ["hello", "world"];
var joined = call string.join(array=arr, delimiter=" ");
print joined; // "hello world"

if call string.contains(str="hello world", substring="world") then {
  print "Found!";
}

if call string.startsWith(str="hello", prefix="hel") then {
  print "Starts with hel";
}

if call string.endsWith(str="hello", suffix="llo") then {
  print "Ends with llo";
}

if call json.isEmpty(value=myJson) then {
